import React, { useState } from 'react';
import "../styles/SearchBar.css";


export const SearchBar = (props) =>{

    let [content, setContent] = useState("")

    let handleChange = (e) =>
    {
        e.preventDefault();
        setContent(e.target.value)
    }

    let handleSubmit = (e) =>
    {
        e.preventDefault();
        let trimmedContent = content.trim();
        if(trimmedContent.length !== 0 && trimmedContent)
        {
            props.setQuery(trimmedContent);
        }
        setContent("");
    }

    const homeStyle = {
        top: "15px",
        right: "15px",
        position: "fixed"
    }

    const labelStyle = {

    }
    const inputStyle = {
        width: "100px",
        height: "20px",
        borderRadius: "5px",
        borderColor: "black"

    }
    const buttonBarStyle = {
        background:"url(/styles/SearchIcon.png)"

    }

    return(
        <div style = {homeStyle}>
        <form onSubmit = {handleSubmit}>
        <label htmlFor = "name" style = {labelStyle}>Search:</label>
            <input type = "text" onChange = {handleChange} value = {content} style = {inputStyle}></input>
            <button id = "submit" style = {buttonBarStyle}></button>
        </form>
        </div>

    );



}
