import React, { useState } from 'react';


export const SearchBar = (props) =>{

    let [content, setContent] = useState("")

    let handleChange = (e) =>
    {
        e.preventDefault();
        setContent(e.target.value)
    }

    let handleSubmit = (e) =>
    {
        e.preventDefault();
        let trimmedContent = content.trim();
        if(trimmedContent.length !== 0 && trimmedContent)
        {
            props.setQuery(trimmedContent);
        }
        setContent("");
    }
    const barStyle = {
      postion: "sticky"

    }

    return(
        <div>
        <form onSubmit = {handleSubmit}>
        <label htmlFor = "name" style = {barStyle}>Search:</label>
            <input type = "text" onChange = {handleChange} value = {content} style = {barStyle}></input>
            <button id = "submit" style = {barStyle}>Submit!</button>
        </form>
        </div>

    );



}
