import React, { useState } from 'react';


export const SearchBar = (props) =>{

    let [content, setContent] = useState("")

    let handleChange = (e) =>
    {
        e.preventDefault();
        setContent(e.target.value)
    }

    let handleSubmit = (e) =>
    {
        e.preventDefault();
        let trimmedContent = content.trim();
        if(trimmedContent.length !== 0 && trimmedContent)
        {
            props.setQuery(trimmedContent);
        }
        setContent("");
    }
    const labelStyle = {
      postion: "relative",
      float: "left",
      fontSize: "15px",
      color: "black"

    }
    const inputStyle = {
      postion: "relative",
      float: "left",
      fontSize: "15px"

    }
    const buttonBarStyle = {
      postion: "relative",
      float: "left",
      fontSize: "15px",
      borderRadius: "2px",
      margin: "4px"

    }

    return(
        <div>
        <form onSubmit = {handleSubmit}>
        <label htmlFor = "name" style = {labelStyle}>Search:</label>
            <input type = "text" onChange = {handleChange} value = {content} style = {inputStyle}></input>
            <button id = "submit" style = {buttonBarStyle}>Submit!</button>
        </form>
        </div>

    );



}
